import React, { Component } from "react";
import { connect } from "react-redux";
import { ListGroup, ListGroupItem } from "reactstrap";
import { bindActionCreators } from "redux";
import * as categoryActions from "../../redux/actions/categoryActions";
import { Badge } from "reactstrap";
import * as productActions from "../../redux/actions/productActions";

class CategoryList extends Component {
  componentDidMount() {
    this.props.actions.getCategories();
  }

  selectCategory = (category) => {
    this.props.actions.changeCategory(category);
    this.props.actions.getProducts(category.id)
  };
  render() {
    return (
      <div>
        <h3>
          <Badge color="warning">Kategoriler</Badge>{" "}
        </h3>
        <ListGroup>
          {this.props.categories.map((category) => (
            <ListGroupItem
              active={category.id === this.props.currentCategory.id}
              onClick={() => this.selectCategory(category)}
              key={category.id}
            >
              {category.categoryName}
            </ListGroupItem>
          ))}
        </ListGroup>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentCategory: state.changeCategoryReducer,
    categories: state.categoryListReducer,
  };
}
function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getCategories: bindActionCreators(
        categoryActions.getCategories,
        dispatch
      ),
      changeCategory: bindActionCreators(
        categoryActions.changeCategory,
        dispatch
      ),
      getProducts: bindActionCreators(productActions.getProducts, dispatch),
    },
  };
}


export default connect(mapStateToProps, mapDispatchToProps)(CategoryList);

function App() {
  
  // State for Active index
  const [activeIndex, setActiveIndex] = React.useState(0);

  // State for Animation
  const [animating, setAnimating] = React.useState(false);

  // Sample items for Carousel
  const items = [
      {src: 
'https://media.geeksforgeeks.org/wp-content/cdn-uploads/20190603152813/ml_gaming.png',
      },
      {src: 
'https://media.geeksforgeeks.org/wp-content/cdn-uploads/20190528184201/gateexam.png',
      }
  ];

  // Items array length
  const itemLength = items.length - 1

  // Previous button for Carousel
  const previousButton = () => {
      if (animating) return;
      const nextIndex = activeIndex === 0 ?
          itemLength : activeIndex - 1;
      setActiveIndex(nextIndex);
  }

  // Next button for Carousel
  const nextButton = () => {
      if (animating) return;
      const nextIndex = activeIndex === itemLength ?
          0 : activeIndex + 1;
      setActiveIndex(nextIndex);
  }

  // Carousel Item Data
  const carouselItemData = items.map((item) => {
      return (
          <CarouselItem
              key={item.src}
              onExited={() => setAnimating(false)}
              onExiting={() => setAnimating(true)}
          >
              <img src={item.src} alt={item.altText} />
          </CarouselItem>
      );
  });

  return (
      <div style={{
          display: 'block', padding: 30
      }}>
          <h1>ReactJS Reactstrap Carousel Component</h1>
          <Carousel previous={previousButton} next={nextButton} 
              activeIndex={activeIndex}>
              <CarouselIndicators items={items}
                  activeIndex={activeIndex}
                  onClickHandler={(newIndex) => {
                      if (animating) return;
                      setActiveIndex(newIndex);
                  }} />
              {carouselItemData}
          </Carousel>
      </div >
  );
}
